import React, { useState } from 'react';
import { Pokemon } from './pokemon'; // Dosya yolu doğru olduğundan emin olun
import { useGetPokemonByNameQuery } from './services/pokemon';

const pokemonList = ['bulbasaur', 'pikachu', 'ditto', 'ivysaur', 'venusaur', 'charmander'];

export default function Api() {
  const [pollingInterval, setPollingInterval] = useState(0);

  const handlePollingIntervalChange = (event) => {
    const selectedValue = event.target.value;
    setPollingInterval(selectedValue === 'ALL' ? 0 : Number(selectedValue));
  };

  return (
    <div className="App">
      <select onChange={handlePollingIntervalChange}>
        <option value="ALL">ALL</option>
        {pokemonList.map((poke, index) => (
          <option key={index} value={poke}>
            {poke}
          </option>
        ))}
      </select>
      <div>
        {pokemonList.map((poke, index) => (
          <Pokemon key={index} name={poke} pollingInterval={pollingInterval} />
        ))}
      </div>
    </div>
  );
}
